const Box = ({ rowNum, boxNum, boxData }) => {
  const evenRow = rowNum % 2 === 0;
  const evenBox = boxNum % 2 === 0;
  const boxColor =
    (evenRow && evenBox) || (!evenRow && !evenBox) ? "black" : "white";
  if (boxData !== "o") {
    const pieceColor = boxData === "t" ? "red" : "black";
  }
  return (
    <div className={`box--${boxColor}`}>
      <div className={`piece--${pieceColor}`}></div>
    </div>
  );
};

export default Box;
