const Box = ({
  board,
  rowNum,
  boxNum,
  boxData,
  circle,
  topColorRed,
  selectedPiece,
  setSelectedPiece,
  potentialMoves,
  setPotentialMoves,
}) => {
  const evenRow = rowNum % 2 === 0;
  const evenBox = boxNum % 2 === 0;
  const boxColor =
    (evenRow && evenBox) || (!evenRow && !evenBox) ? "black" : "white";

  const isPotentialMove = () => {
    return potentialMoves.some((el) => {
      return el[0] === boxNum && el[1] === rowNum;
    });
  };
  const potentialMove = isPotentialMove() ? "potential" : "";

  if (boxData === "o") {
    return <div className={`box--${boxColor} ${potentialMove}`} />;
  }
  const pieceColor =
    (topColorRed && boxData === "t") || (!topColorRed && boxData === "b")
      ? "red"
      : "black";
  const pieceShape = circle ? "circle" : "";
  const pieceSelected =
    selectedPiece[0] === boxNum && selectedPiece[1] === rowNum
      ? "selected"
      : "";

  const checkMoves = (array) => {
    return array.reduce((acc, [x, y]) => {
      if (x >= 0 || x < board.length || board[y][x] === "o") {
        acc.push([x, y]);
      }
      return acc;
    }, []);
  };
  const handleSelect = () => {
    setSelectedPiece([boxNum, rowNum]);
    setPotentialMoves(() => {
      if (boxData === "t") {
        const left = [boxNum - 1, rowNum + 1];
        const right = [boxNum + 1, rowNum + 1];
        const newPotentialMoves = checkMoves([left, right]);
        if (boxNum === 0) {
          newPotentialMoves.push(left);
        } else if (boxNum === board.length - 1) {
          newPotentialMoves.push(right);
        } else {
          newPotentialMoves.push(left);
          newPotentialMoves.push(right);
        }
      } else if (boxData === "b") {
        const left = [boxNum - 1, rowNum - 1];
        const right = [boxNum + 1, rowNum - 1];
        const newPotentialMoves = checkMoves([left, right]);
        if (boxNum === 0) {
          newPotentialMoves.push(right);
        } else if (boxNum === board.length - 1) {
          newPotentialMoves.push(left);
        } else {
          newPotentialMoves.push(left);
          newPotentialMoves.push(right);
        }
      }
      return newPotentialMoves;
    });
  };
  return (
    <div className={`box--${boxColor}`}>
      <div
        className={`piece--${pieceColor} ${pieceShape} ${pieceSelected}`}
        onClick={handleSelect}
      />
    </div>
  );
};

export default Box;
