import { useState, useEffect } from "react";
import Row from "../components/Row";

const App = () => {
  const [rowNum, setRowNum] = useState(8);
  const [inputRows, setInput] = useState(8);
  const [board, setBoard] = useState([]);
  useEffect(() => {
    setBoard(() => {
      const newBoard = [];
      for (let i = 0; i < rowNum; i++) {
        newBoard.push([]);
        for (let j = 0; j < rowNum; j++) {
          newBoard[i].push("o");
        }
      }
      return newBoard;
    });
  }, [rowNum]);

  return (
    <div className="main">
      <h3>Number of Board Rows</h3>
      <input
        type="number"
        value={inputRows}
        onChange={(e) => {
          setInput(e.target.value);
        }}
      />
      <button
        onClick={() => {
          setRowNum(inputRows);
        }}
      >
        Submit
      </button>
      {board.map((el, rowNum) => (
        <Row rowData={el} rowNum={rowNum} />
      ))}
    </div>
  );
};

export default App;
