const Box = ({ rowNum, boxNum, boxData }) => {
  const evenRow = rowNum % 2 === 0;
  const evenBox = boxNum % 2 === 0;
  let boxColor = "white";
  if (evenRow) {
    if (evenBox) {
      boxColor = "black";
    }
  } else {
    if (!evenBox) {
      boxColor = "black";
    }
  }

  return <div className={`box--${boxColor}`}></div>;
};

export default Box;
