const Box = ({ rowNum, boxNum, boxData, circle, topColorRed }) => {
  const evenRow = rowNum % 2 === 0;
  const evenBox = boxNum % 2 === 0;
  const boxColor =
    (evenRow && evenBox) || (!evenRow && !evenBox) ? "black" : "white";

  if (boxData === "o") {
    return <div className={`box--${boxColor}`} />;
  }
  const pieceColor = topColorRed && boxData === "t" ? "red" : "black";
  const pieceShape = circle ? "circle" : "";
  const piece = <div className={`piece--${pieceColor} ${pieceShape}`}></div>;

  return <div className={`box--${boxColor}`}>{piece}</div>;
};

export default Box;
