import { useState } from "react";
import Row from "../components/Row";

const App = () => {
  const [rowNum, setRowNum] = useState(8);
  const [inputRows, setInput] = useState(8);
  const board = new Array(rowNum);
  const row = new Array(rowNum);
  row.fill("o");
  board.fill(row);

  return (
    <div className="main">
      <h3>Number of Board Rows</h3>
      <input
        type="number"
        value={inputRows}
        onChange={(e) => {
          setInput(e.target.value);
        }}
      />
      <button
        onClick={() => {
          setRowNum(inputRows);
        }}
      >
        Submit
      </button>
      {board.map((el, rowNum) => (
        <Row rowData={el} rowNum={rowNum} />
      ))}
    </div>
  );
};

export default App;
