import { useState, useEffect } from "react";
import Row from "../components/Row";

const App = () => {
  const [rowNum, setRowNum] = useState(8);
  const [inputRows, setInput] = useState(8);
  const [board, setBoard] = useState([]);
  const [circle, setShape] = useState(true);
  useEffect(() => {
    setBoard(() => {
      const newBoard = [];
      for (let i = 0; i < rowNum; i++) {
        newBoard.push([]);
        for (let j = 0; j < rowNum; j++) {
          if (i <= 1) {
            newBoard[i].push("t");
          } else if (i >= rowNum - 2) {
            newBoard[i].push("b");
          } else {
            newBoard[i].push("o");
          }
        }
      }
      return newBoard;
    });
  }, [rowNum]);
  return (
    <div className="main">
      <h3>Number of Board Rows</h3>
      <input
        type="number"
        value={inputRows}
        onChange={(e) => {
          setInput(e.target.value);
        }}
      />
      <button
        onClick={() => {
          setRowNum(inputRows);
        }}
      >
        Submit
      </button>
      <h3>Select your shape</h3>
      <div className="shape-toggle">
        <input
          type="radio"
          name="shape"
          value={true}
          onChange={() => {
            setShape(true);
          }}
          defaultChecked
        />
        <label for="circle">Circle</label>
        <input
          type="radio"
          name="shape"
          value={false}
          onChange={() => {
            setShape(false);
          }}
        />
        <label for="square">Square</label>
      </div>
      {board.map((el, rowNum) => (
        <Row rowData={el} rowNum={rowNum} circle={circle} />
      ))}
    </div>
  );
};

export default App;
